  `id_recette` int(3) NOT NULL COMMENT 'Identifiant des recettes',
  `titre_recette` varchar(100) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT 'titre de la recette',
  `description_recette` varchar(250) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT 'description rapide de la recette',
  `image_recette` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT 'neutre.jpg',
  `ingredient_recette` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT 'Liste des ingrédients de la recette',
  `explication_recette` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT 'Description de la recette',
  `lien_recette` varchar(100) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT 'Lien vidéo vers la recette au cas où l''explication ne serait pas suffisante.'
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Liste des recettes';

